// Prepare data for HubSpot sync
return $input.all().map(item => {
  const data = item.json;
  const fullName = data.fullName || '';
  const nameParts = fullName.split(' ');
  const firstName = data.firstName || '';
  const lastName = data.lastName || '';
  const companyName = data.company || '';

  // Generate placeholder email
  const placeholderEmail = fullName
    ? fullName.toLowerCase().replace(/[^a-z0-9]/g, '.').replace(/\.+/g, '.').replace(/^\.|\.$/, '') + '@placeholder.linkedin'
    : 'unknown@placeholder.linkedin';

  // Generate pseudo-domain
  const domain = companyName
    ? companyName.toLowerCase().replace(/[^a-z0-9]/g, '').substring(0, 30) + '.com'
    : '';

  return {
    json: {
      ...data,
      _hs: {
        firstName: firstName,
        lastName: lastName,
        placeholderEmail: placeholderEmail,
        domain: domain
      }
    }
  };
});

// Extract company ID from search result (existing company)
const searchResult = $('Search Company').item.json;
const preparedData = $('Prepare HubSpot Data').item.json;

return [{
  json: {
    ...preparedData,
    _hs: {
      ...preparedData._hs,
      companyId: searchResult.results[0].id,
      companyCreated: false
    }
  }
}];

// Extract company ID from create result (new company)
