// Filter out manual triggers and extract error info
const data = $input.first().json;

// Check if this was a manual trigger - skip those
const mode = data.execution?.mode || data.mode || '';
if (mode === 'manual' || mode === 'trigger') {
  return [{ json: { skip: true, reason: 'Manual trigger - not monitoring' } }];
}

// Extract error details
const workflowId = data.workflow?.id || data.workflowId || 'unknown';
const workflowName = data.workflow?.name || data.workflowName || 'Unknown Workflow';
const executionId = data.execution?.id || data.executionId || 'unknown';
const errorMessage = data.execution?.error?.message || data.error?.message || 'Unknown error';
const errorNode = data.execution?.error?.node?.name || data.error?.node || 'Unknown node';
const errorStack = data.execution?.error?.stack || data.error?.stack || '';
const startedAt = data.execution?.startedAt || new Date().toISOString();

return [{
  json: {
    skip: false,
    workflowId,
    workflowName,
    executionId,
    errorMessage,
    errorNode,
    errorStack,
    startedAt,
    mode,
    timestamp: new Date().toISOString()
  }
}];

// Format error for logging and notification
const error = $input.first().json;

const formattedError = {
  summary: `Error in ${error.workflowName} at node '${error.errorNode}'`,
  details: {
    workflow: {
      id: error.workflowId,
      name: error.workflowName
    },
    execution: {
      id: error.executionId,
      startedAt: error.startedAt,
      mode: error.mode
    },
    error: {
      node: error.errorNode,
      message: error.errorMessage,
      stack: error.errorStack.substring(0, 500) // Truncate stack
    }
  },
  // For the auto-debug script
  debugContext: {
    workflowId: error.workflowId,
    executionId: error.executionId,
    errorNode: error.errorNode,
    errorMessage: error.errorMessage
  },
  timestamp: error.timestamp
};

return [{ json: formattedError }];

// Log that local debugger wasn't available
const error = $('Format Error').first().json;
return [{
  json: {
    status: 'logged',
    message: 'Error logged but local debugger not available',
    error: error
  }
}];
