// Parse error data from Error Trigger node
const data = $input.first().json;

// Error Trigger provides data directly (not nested like webhooks)
const execution = data.execution || {};
const workflow = data.workflow || {};

// Check if this was a manual trigger - skip those
const mode = execution.mode || '';
if (mode === 'manual') {
  return [{ json: { skip: true, reason: 'Manual trigger - not monitoring' } }];
}

// Extract error details from Error Trigger format
const workflowId = workflow.id || 'unknown';
const workflowName = workflow.name || 'Unknown Workflow';
const executionId = execution.id || 'unknown';
const errorMessage = execution.error?.message || 'Unknown error';
const errorNode = execution.error?.node?.name || execution.lastNodeExecuted || 'Unknown node';
const errorStack = execution.error?.stack || '';
const startedAt = execution.startedAt || new Date().toISOString();

return [{
  json: {
    skip: false,
    workflowId,
    workflowName,
    executionId,
    errorMessage,
    errorNode,
    errorStack,
    startedAt,
    mode,
    timestamp: new Date().toISOString()
  }
}];

// Format error for logging and notification
const error = $input.first().json;

const formattedError = {
  summary: `Error in ${error.workflowName} at node '${error.errorNode}'`,
  details: {
    workflow: {
      id: error.workflowId,
      name: error.workflowName
    },
    execution: {
      id: error.executionId,
      startedAt: error.startedAt,
      mode: error.mode
    },
    error: {
      node: error.errorNode,
      message: error.errorMessage,
      stack: (error.errorStack || '').substring(0, 500)
    }
  },
  debugContext: {
    workflowId: error.workflowId,
    executionId: error.executionId,
    errorNode: error.errorNode,
    errorMessage: error.errorMessage
  },
  debugCommand: `cd /Users/hudsonlorfing/Documents/Business/Chrt/workflows/chrt-n8n-workflows && ./scripts/auto-debug.sh analyze ${error.executionId}`,
  timestamp: error.timestamp
};

return [{ json: formattedError }];

// Log that local debugger wasn't available
const error = $('Format Error').first().json;

const logEntry = {
  status: 'logged_locally',
  message: 'Local debugger not available. Error logged in n8n execution history.',
  summary: error.summary,
  debugCommand: error.debugCommand,
  n8nUrl: `https://chrt.app.n8n.cloud/workflow/${error.details.workflow.id}/executions/${error.details.execution.id}`,
  timestamp: new Date().toISOString()
};

return [{ json: logEntry }];
