// Extract meetingId from Fireflies webhook payload
const webhook = $input.first().json;
const body = webhook.body || webhook;
const meetingId = body.meetingId || body.meeting_id || body.id;

if (!meetingId) {
  throw new Error('No meetingId in webhook payload: ' + JSON.stringify(body).substring(0, 200));
}

console.log('Received Fireflies webhook for meeting:', meetingId);
return [{ json: { meetingId, eventType: body.eventType || 'Transcription completed' } }];

const response = $input.first().json;
const transcript = response.data?.transcript;

if (!transcript) {
  const error = response.errors?.[0]?.message || 'Transcript not found';
  throw new Error('Fireflies API error: ' + error);
}

// Build transcript text from sentences
const transcriptText = (transcript.sentences || []).map(s => `${s.speaker_name}: ${s.text}`).join('\n');

// Parse date
const dateMs = transcript.date || Date.now();
const dateStr = new Date(dateMs).toISOString().split('T')[0];
const durationMins = Math.round(transcript.duration || 0);

// Generate hash
const hash = require('crypto').createHash('md5').update(transcript.id + transcript.title).digest('hex').substring(0, 8);

const title = transcript.title || 'Untitled Meeting';
const attendees = transcript.participants || [];

// Extract person name
let personName = '';
const titleLower = title.toLowerCase();
const withMatch = title.match(/(?:with|w\/|and|&|x)\s+([A-Z][a-z]+(?:\s+[A-Z][a-z]+)*)/i);
if (withMatch) {
  personName = withMatch[1].trim();
} else {
  personName = attendees.find(a => !a.toLowerCase().includes('hudson')) || '';
  if (personName.includes('@')) {
    personName = personName.split('@')[0].split('.').map(p => p.charAt(0).toUpperCase() + p.slice(1)).join(' ');
  }
}
if (!personName) personName = title.replace(/^(?:chat|call|meeting|sync|1:1|1-1)\s*/i, '').trim();

const firefliesUrl = `https://app.fireflies.ai/view/${transcript.id}`;
const transcriptLength = transcriptText.length;
const estimatedTokens = Math.ceil(transcriptLength / 4);

return [{ json: {
  meetingId: transcript.id,
  hash,
  title,
  personName,
  date: new Date(dateMs).toISOString(),
  dateStr,
  duration: durationMins * 60,
  durationMins,
  attendees: attendees.join(', '),
  attendeesList: attendees,
  transcript: transcriptText,
  transcriptLength,
  estimatedTokens,
  firefliesUrl,
  summary: transcript.summary?.overview || ''
} }];

const meeting = $('Process Transcript').first().json;
const detection = $input.first().json;

// Merge meeting data with detection results
const combined = {
  ...meeting,
  workspace: detection.workspace || 'chrt',
  workspaceConfidence: detection.workspaceConfidence || 'low',
  isExternal: detection.isExternal || false,
  meetingType: detection.meetingType || 'general',
  suggestedApps: detection.suggestedApps || [{ id: 'general-notes', weight: 1.0 }],
  appConfidence: detection.appConfidence || 'low'
};

return [{ json: combined }];
