// Extract all JSON files from the workflows/ directory
const tree = $input.first().json.tree || [];
const workflowsPath = $('Set GitHub Details').first().json.repo_workflows_path;

// Filter to only JSON files within the workflows directory
const jsonFiles = tree.filter(item => {
  return item.type === 'blob' && 
         item.path.startsWith(workflowsPath + '/') && 
         item.path.endsWith('.json');
});

// Return each file as a separate item
return jsonFiles.map(file => ({
  json: {
    path: file.path,
    sha: file.sha,
    name: file.path.split('/').pop()
  }
}));

// Process ALL input items, not just the first one
const allInputs = $input.all();
const allDecoded = $('Decode to json').all();

// For each comparison result, find the matching GitHub workflow
const results = [];
for (const input of allInputs) {
  const workflowId = input.json.same?.id;
  const matched = allDecoded.find(item => item.json?.id === workflowId);
  if (matched) {
    results.push({ json: { dataset: matched.json } });
  }
}

return results;

// Process ALL input items, not just the first one
const allInputs = $input.all();
const allN8n = $('Build Folder Paths1').all();
const allDecoded = $('Decode to json').all();
const repoPath = $('Set GitHub Details').first().json.repo_workflows_path;

// For each comparison result, find the matching n8n workflow (with folder paths)
const results = [];
for (const input of allInputs) {
  const workflowId = input.json.same?.id;
  const matched = allN8n.find(item => item.json?.id === workflowId);
  const githubFile = allDecoded.find(item => item.json?.id === workflowId);

  if (matched) {
    // Calculate expected path based on folder
    const folderPath = matched.json._folderPath || '';
    const fileName = matched.json.name.trim().replace(/\s+/g, '-').toLowerCase() + '.json';
    const expectedPath = folderPath ? `${repoPath}/${folderPath}/${fileName}` : `${repoPath}/${fileName}`;
    const currentGithubPath = githubFile?.json?._githubPath || '';
    const pathChanged = expectedPath !== currentGithubPath;

    results.push({ 
      json: { 
        dataset: matched.json,
        _currentGithubPath: currentGithubPath,
        _expectedPath: expectedPath,
        _pathChanged: pathChanged
      } 
    });
  }
}

return results;

// Split aggregated items back into individual items for comparison
const aggregated = $input.first().json.data || [];

// Return each workflow as a separate item
return aggregated.map(item => ({ json: item }));

// Build folder path from workflow tags
// Priority order for folder: linkedin, hubspot, or root
const folderTags = ['linkedin', 'hubspot', 'connections'];

return $input.all().map(item => {
  const workflow = item.json;
  const tags = workflow.tags || [];

  // Find the first tag that matches our folder structure
  let folderName = '';
  for (const tag of tags) {
    const tagName = tag.name?.toLowerCase();
    if (folderTags.includes(tagName)) {
      folderName = tagName;
      break;
    }
  }

  // Build the folder path
  const folderPath = folderName ? folderName : '';

  return {
    json: {
      ...workflow,
      _folderPath: folderPath,
      _fileName: workflow.name.trim().replace(/\s+/g, '-').toLowerCase() + '.json'
    }
  };
});

