// Count total remaining items before limiting
const items = $input.all();
const total = items.length;

// Pass through all items with count attached
return items.map((item, index) => ({
  json: {
    ...item.json,
    _batchInfo: {
      totalRemaining: total,
      batchSize: Math.min(50, total),
      itemIndex: index + 1
    }
  }
}));

// Get original PhantomBuster data and AI scoring result
const pb = $('Limit to 50/run').item.json;
let aiResult = { score: 5, segment: 'Unknown', reason: 'Could not parse AI response' };

try {
  const content = $('AI Score Lead (ICP)').item.json.message?.content || '{}';
  const parsed = JSON.parse(content.replace(/```json?|```/g, '').trim());
  aiResult = {
    score: parsed.score ?? 5,
    segment: parsed.segment || 'Unknown',
    reason: parsed.reason || 'No reason provided'
  };
} catch (e) {}

// Map PhantomBuster fields to Master List format
return [{
  json: {
    profileUrl: pb.profileUrl || pb.linkedinProfileUrl || '',
    fullName: pb.fullName || `${pb.firstName || ''} ${pb.lastName || ''}`.trim(),
    jobTitle: pb.jobTitle || '',
    company: pb.company || pb.companyName || '',
    industry: pb.industry || '',
    headline: pb.headline || '',
    location: pb.location || '',
    connectionDegree: pb.connectionDegree || '',
    previousRole: pb.jobTitle2 ? `${pb.jobTitle2} @ ${pb.company2 || ''}` : '',
    score: aiResult.score,
    segment: aiResult.segment,
    reason: aiResult.reason,
    status: aiResult.score >= 6 ? 'Ready' : 'Low Priority',
    added: new Date().toISOString().split('T')[0],
    _batchInfo: pb._batchInfo
  }
}];

// Summarize batch results
const items = $input.all();
const firstItem = items[0]?.json || {};
const batchInfo = firstItem._batchInfo || { totalRemaining: 0, batchSize: 0 };

const processed = items.length;
const remaining = batchInfo.totalRemaining - processed;

// Count by status
const ready = items.filter(i => i.json.status === 'Ready').length;
const lowPriority = items.filter(i => i.json.status === 'Low Priority').length;

return [{
  json: {
    message: remaining > 0 
      ? `Batch complete. Run again to process remaining ${remaining} leads.`
      : `All done! No more leads to process. Clear 'New Leads' sheet when ready.`,
    processed: processed,
    remaining: remaining,
    ready: ready,
    lowPriority: lowPriority,
    runsNeeded: Math.ceil(remaining / 50)
  }
}];
