// Determine batch size based on trigger source
// Default: 10 for manual, 240 for webhook (pipeline monitor)
let batchSize = 10;

try {
  // Check if triggered via webhook with batchSize parameter
  const inputData = $input.first()?.json || {};
  if (inputData.batchSize) {
    batchSize = parseInt(inputData.batchSize) || 240;
  }
  // Also check body property (n8n webhook wraps data sometimes)
  if (inputData.body && inputData.body.batchSize) {
    batchSize = parseInt(inputData.body.batchSize) || 240;
  }
} catch (e) {
  // Manual trigger - use default
  batchSize = 10;
}

return [{ json: { batchSize, source: batchSize > 10 ? 'pipeline-monitor' : 'manual' } }];

const pb = $('Loop Over Leads1').item.json;
let aiResult = { score: 5, segment: 'Unknown', reason: 'Could not parse AI response', hubspotIndustry: '' };

try {
  const content = $('AI ICP Score').item.json.content?.[0]?.text || '{}';
  const parsed = JSON.parse(content.replace(/```json?|```/g, '').trim());
  aiResult = {
    score: parsed.score ?? 5,
    segment: parsed.segment || 'Unknown',
    reason: parsed.reason || 'No reason provided',
    hubspotIndustry: parsed.hubspotIndustry || ''
  };
} catch (e) {}

// Output all 34 columns in order A-AH
return [{
  json: {
    profileUrl: pb.profileUrl || pb.linkedinProfileUrl || '',
    fullName: pb.fullName || `${pb.firstName || ''} ${pb.lastName || ''}`.trim(),
    firstName: pb.firstName || '',
    lastName: pb.lastName || '',
    school: pb.school || '',
    schoolDegree: pb.schoolDegree || '',
    company: pb.company || pb.companyName || '',
    companyUrl: pb.companyUrl || '',
    companyId: pb.companyId || '',
    jobTitle: pb.jobTitle || '',
    jobDateRange: pb.jobDateRange || '',
    'adj industry': aiResult.hubspotIndustry,
    headline: pb.headline || '',
    location: pb.location || '',
    company2: pb.company2 || '',
    companyUrl2: pb.companyUrl2 || '',
    companyId2: pb.companyId2 || '',
    jobTitle2: pb.jobTitle2 || '',
    jobDateRange2: pb.jobDateRange2 || '',
    score: aiResult.score,
    segment: aiResult.segment,
    reason: aiResult.reason,
    status: aiResult.score >= 6 ? 'Ready' : 'Low Priority',
    added: new Date().toISOString().split('T')[0],
    kyleSentDate: '',
    kyleConnectDate: '',
    hudsonSentDate: '',
    hudsonConnectDate: '',
    hubspotSync: '',
    hudsonNotes: '',
    inviteResults: '',
    connectionDegree: pb.connectionDegree || '',
    previousRole: pb.jobTitle2 ? `${pb.jobTitle2} @ ${pb.company2 || ''}` : '',
    industry: pb.industry || ''
  }
}];

// Data comes from Merge node (append mode)
// Input 0: New Leads, Input 1: Master List
// The merge appends both, so we need to identify them by structure

// All items from the merge
const allItems = $input.all();

// New Leads have 'linkedinProfileUrl' or 'profileUrl', Master List has 'profileUrl'
// Master List items have 'score' field (from previous processing)
const newLeads = [];
const masterUrls = new Set();

allItems.forEach(item => {
  const data = item.json;
  // Master List items have 'score' or 'segment' fields
  if (data.score !== undefined || data.segment !== undefined) {
    const url = (data.profileUrl || '').trim().toLowerCase();
    if (url) masterUrls.add(url);
  } else {
    // This is a new lead
    newLeads.push(item);
  }
});

// Filter new leads to only those not in master list
const uniqueLeads = newLeads.filter(lead => {
  const url = (lead.json.linkedinProfileUrl || lead.json.profileUrl || '').trim().toLowerCase();
  return url && !masterUrls.has(url);
});

return uniqueLeads;

