// Prepare data for HubSpot sync - now with real emails from Profile Scraper
const inputItems = $input.all();

// Check if we have input data
if (!inputItems || inputItems.length === 0) {
  throw new Error('No input data received from previous node');
}

return inputItems.map(item => {
  // Validate item structure
  if (!item || !item.json) {
    throw new Error('Invalid input item structure - missing json property');
  }

  const data = item.json;
  const fullName = data.fullName || '';
  const firstName = data.firstName || '';
  const lastName = data.lastName || '';
  const companyName = data.companyName || data.company || '';

  // Use real email from Profile Scraper if available, otherwise generate placeholder
  const realEmail = data.email || '';
  const placeholderEmail = fullName
    ? fullName.toLowerCase().replace(/[^a-z0-9]/g, '.').replace(/\.+/g, '.').replace(/^\.|\.$/, '') + '@placeholder.linkedin'
    : 'unknown@placeholder.linkedin';
  const finalEmail = realEmail || placeholderEmail;
  const hasRealEmail = !!realEmail;

  // Use company website from Profile Scraper if available, otherwise generate pseudo-domain
  const companyWebsite = data.companyWebsite || '';
  const domain = companyWebsite
    ? companyWebsite.replace(/^https?:\/\//, '').replace(/^www\./, '').split('/')[0]
    : (companyName ? companyName.toLowerCase().replace(/[^a-z0-9]/g, '').substring(0, 30) + '.com' : '');

  return {
    json: {
      ...data,
      _hs: {
        firstName: firstName,
        lastName: lastName,
        email: finalEmail,
        hasRealEmail: hasRealEmail,
        placeholderEmail: placeholderEmail,
        domain: domain,
        companyWebsite: companyWebsite,
        companySize: (data.companySize || '').toString().replace(/[^0-9]/g, '') || '',
        companyIndustry: data.companyIndustry || data.industry || '',
        companyDescription: data.companyDescription || ''
      }
    }
  };
});

// Extract company ID from search result (existing company)
