const scraperItems = $input.all();
let scraperProfiles = [];

scraperItems.forEach(item => {
  const j = item.json || {};

  // Case 1: Webhook input with profiles in 'body' array (or body.body when webhook wraps POST)
  const bodyArray = Array.isArray(j.body) ? j.body : (j.body && Array.isArray(j.body.body) ? j.body.body : null);
  if (bodyArray) {
    bodyArray.forEach(bodyItem => {
      const profile = bodyItem.json || bodyItem;
      if (profile.profileUrl || profile.linkedinProfileUrl) {
        scraperProfiles.push(profile);
      }
    });
  }
  // Case 2: Direct profile with profileUrl/linkedinProfileUrl
  else if (j.profileUrl || j.linkedinProfileUrl) {
    scraperProfiles.push(j);
  }
  // Case 3: Phantombuster output with embedded profile array in 'output' field
  else if (Array.isArray(j.output)) {
    scraperProfiles.push(...j.output.filter(p => p.profileUrl || p.linkedinProfileUrl));
  }
  // Case 4: Single profile in 'output' object
  else if (j.output && (j.output.profileUrl || j.output.linkedinProfileUrl)) {
    scraperProfiles.push(j.output);
  }
});

// Create a map of scraper data by normalized URL
const scraperMap = new Map();
scraperProfiles.forEach(p => {
  let url = (p.profileUrl || p.linkedinProfileUrl || '').trim().toLowerCase();
  url = url.replace('https://www.linkedin.com', 'https://linkedin.com').replace(/\/$/, '');
  if (url) scraperMap.set(url, p);
});

