      companyName: p.companyName || p.linkedinCompanyName || '',
      companyWebsite: p.companyWebsite || p.linkedinCompanyWebsite || '',
      companySize: p.linkedinCompanySize || '',
      companyIndustry: p.companyIndustry || p.linkedinCompanyIndustry || '',
      hsIndustry: p.hsIndustry || '',
      companyDescription: p.linkedinCompanyDescription || '',
      companyLinkedinUrl: p.linkedinCompanyUrl || '',
      jobTitle: p.linkedinJobTitle || '',
      jobDateRange: p.linkedinJobDateRange || '',
      connectionDegree: p.connectionDegree || '',
      _source: 'scraper_direct'
