// Extract Dashboard metrics
const data = $input.first().json;

// B5 = Pre-processed count, D4 = Potential Connections
// Google Sheets returns values by column letter
const preProcessed = parseInt(data['Pre-Processed'] || data[Object.keys(data)[0]] || 0);
const potentialConnections = parseInt(data['Potential Connections'] || data[Object.keys(data)[2]] || 0);

return [{
  json: {
    preProcessed,
    potentialConnections,
    needsIngestion: potentialConnections < 240,
    needsScraping: preProcessed < 1000,
    timestamp: new Date().toISOString()
  }
}];

// Analyze People to Crawl data
// Columns: A=Profile Type, B=Name, C=Search Link, D=Last Crawl Date
const allRows = $input.all();

// Filter rows with a Search Link (Column C)
const rowsWithLinks = allRows.filter(item => {
  const link = item.json['Search Link'] || item.json['C'] || '';
  return link && link.trim().length > 0;
});

// Separate uncrawled (no date in D) from crawled
const uncrawled = [];
const crawled = [];

rowsWithLinks.forEach((item, index) => {
  const lastCrawl = item.json['Last Crawl Date'] || item.json['D'] || '';
  const searchLink = item.json['Search Link'] || item.json['C'] || '';
  const name = item.json['Name'] || item.json['B'] || '';
  const rowNumber = item.json.row_number || index + 2; // +2 for header row

  const rowData = {
    rowNumber,
    name,
    searchLink: searchLink.trim(),
    lastCrawlDate: lastCrawl ? lastCrawl.trim() : null
  };

  if (!lastCrawl || lastCrawl.trim() === '') {
    uncrawled.push(rowData);
  } else {
    crawled.push(rowData);
  }
});

// Sort crawled by date (oldest first) for fallback
crawled.sort((a, b) => {
  const dateA = new Date(a.lastCrawlDate || '2099-12-31');
  const dateB = new Date(b.lastCrawlDate || '2099-12-31');
  return dateA - dateB;
});

const uncrawledCount = uncrawled.length;
let selectedURL = null;
let selectedRow = null;
let alertLevel = 'none'; // none, warning, critical, fallback

if (uncrawledCount > 2) {
  // Normal operation - use first uncrawled
  selectedURL = uncrawled[0].searchLink;
  selectedRow = uncrawled[0].rowNumber;
  alertLevel = 'none';
} else if (uncrawledCount > 0) {
  // 1-2 URLs left - warning
  selectedURL = uncrawled[0].searchLink;
  selectedRow = uncrawled[0].rowNumber;
  alertLevel = 'warning';
} else if (crawled.length > 0) {
  // No uncrawled - fallback to oldest crawled
  selectedURL = crawled[0].searchLink;
  selectedRow = crawled[0].rowNumber;
  alertLevel = 'fallback';
} else {
  // No URLs at all
  alertLevel = 'critical';
}

return [{
  json: {
    uncrawledCount,
    crawledCount: crawled.length,
    totalURLs: rowsWithLinks.length,
    selectedURL,
    selectedRow,
    alertLevel,
    selectedName: selectedURL ? (uncrawledCount > 0 ? uncrawled[0].name : crawled[0]?.name) : null,
    oldestCrawledDate: crawled[0]?.lastCrawlDate || null
  }
}];

