// Parse PhantomBuster JSON fetched from S3 URL
// n8n HTTP Request auto-splits JSON arrays into multiple items
// Use $input.all() to collect ALL profile items
const allItems = $input.all();

// Debug: Check what we received
const debugInfo = {
  itemCount: allItems.length,
  firstItemKeys: allItems.length > 0 ? Object.keys(allItems[0].json || {}) : [],
  hasLinkedinUrl: allItems.length > 0 ? !!(allItems[0].json?.linkedinProfileUrl || allItems[0].json?.profileUrl) : false
};

// Get selectedName from Analyze URLs & Select Next node
let selectedRow, selectedURL, selectedName;
try {
  const analyzeData = $('Analyze URLs & Select Next').first().json;
  selectedRow = analyzeData.selectedRow;
  selectedURL = analyzeData.selectedURL;
  selectedName = analyzeData.selectedName;
} catch (e) {
  // Manual trigger - these won't be available
  selectedRow = null;
  selectedURL = null;
  selectedName = null;
}

// Each item is already a profile - filter and normalize
const profiles = allItems
  .filter(item => {
    const j = item.json || {};
    return j.linkedinProfileUrl || j.profileUrl;
  })
  .map(item => {
    const p = item.json;
    // Normalize URL - remove www and trailing slash for consistency
    let url = (p.linkedinProfileUrl || p.profileUrl || '').trim().toLowerCase();
    url = url.replace('https://www.linkedin.com', 'https://linkedin.com').replace(/\/$/, '');

    // Return ALL fields from PhantomBuster, plus normalized profileUrl
    return {
      ...p,
      profileUrl: url
    };
  });

return [{
  json: {
    profiles,
    profileCount: profiles.length,
    selectedRow,
    selectedURL,
    selectedName,
    phantomStatus: 'finished',
    _debug: debugInfo
  }
}];

// Deduplicate phantom results against Master List and New Leads
// Data comes from Merge node (append mode) which combines both sheets
const phantomData = $('Merge Search + Scraper Data').first().json;
const mergedItems = $input.all();

// Helper function to normalize LinkedIn URLs for comparison
function normalizeUrl(url) {
  if (!url) return '';
  return url.trim().toLowerCase()
    .replace('https://www.linkedin.com', 'https://linkedin.com')
    .replace(/\/$/, ''); // remove trailing slash
}

// Create a single Set for all existing URLs (from both Master and New Leads)
const existingUrls = new Set();

mergedItems.forEach(item => {
  const url = normalizeUrl(item.json.profileUrl);
  if (url) existingUrls.add(url);
});

// Filter phantom profiles - keep only those not already in Master or New Leads
const uniqueProfiles = phantomData.profiles.filter(profile => {
  const url = normalizeUrl(profile.profileUrl);
  return url && !existingUrls.has(url);
});

return [{
  json: {
    uniqueProfiles,
    uniqueCount: uniqueProfiles.length,
    totalScraped: phantomData.profileCount,
    duplicatesRemoved: phantomData.profileCount - uniqueProfiles.length,
    selectedRow: phantomData.selectedRow,
    selectedURL: phantomData.selectedURL,
    selectedName: phantomData.selectedName
  }
}];

// Prepare row data for updating Last Crawl Date using Name as match field
// Get selectedName from multiple sources - try each in order
let searchUrl = '';

// Try getting from Parse Phantom Output first
try {
  searchUrl = $('Has URL to Process?').first().json.selectedURL || '';
} catch (e) {}

// Only return data if we have a valid name to match on
if (!searchUrl) {
  return [{ json: { _skip: true, reason: 'No selectedName available - skipping crawl date update' } }];
}

return [{
  json: {
    'searchUrl': searchUrl,
    'Last Crawl Date': new Date().toISOString().split('T')[0]
  }
}];

// Extract Dashboard metrics
