// B5 = Pre-processed count, D4 = Potential Connections
// Google Sheets returns values by column letter
const preProcessed = parseInt(data['Pre-Processed'] || data[Object.keys(data)[0]] || 0);

return [{
  json: {
    preProcessed,
    stillNeedsScraping: preProcessed < 1000,
    timestamp: new Date().toISOString()
  }
}];

// Extract profile URLs from Search Export results for Profile Scraper
const phantomData = $input.first().json;
const profiles = phantomData.profiles || [];

// Extract just the profile URLs for the scraper
const profileUrls = profiles
  .map(p => p.linkedinProfileUrl || '')
  .filter(url => url && url.includes('linkedin.com'));

// Store the original search data for later merging
return [{
  json: {
    profileUrls,
    profileCount: profileUrls.length,
    searchData: profiles,
    selectedRow: phantomData.selectedRow,
    selectedURL: phantomData.selectedURL,
    selectedName: phantomData.selectedName
  }
}];

