// Normalize data from either Gmail or Squarespace
const items = $input.all();
const results = [];

for (const item of items) {
  const data = item.json;
  let normalized = {};

  // Check if from Gmail
  if (data.textPlain || data.snippet || data.from) {
    normalized = {
      source: 'gmail',
      raw_text: data.textPlain || data.snippet || '',
      from_email: data.from?.emailAddress || '',
      subject: data.subject || '',
      received_at: new Date().toISOString()
    };
  }
  // Check if from Squarespace
  else if (data.formSubmissionId || data.submittedOn) {
    const fields = data.data || data.formData || data;
    normalized = {
      source: 'squarespace',
      first_name: fields.firstName || fields.first_name || fields['First Name'] || '',
      last_name: fields.lastName || fields.last_name || fields['Last Name'] || '',
      email: fields.email || fields.Email || '',
      phone: fields.phone || fields.Phone || fields.telephone || '',
      address: fields.address || fields.Address || fields.property_address || '',
      raw_text: JSON.stringify(fields),
      received_at: data.submittedOn || new Date().toISOString()
    };
  }

  results.push({ json: normalized });
}

return results;

