// Parse AI response and extract JSON
const aiResponse = $input.first().json.message?.content || $input.first().json.text || '';

try {
  // Try to find JSON in the response
  let jsonStr = aiResponse;
  const jsonMatch = aiResponse.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    jsonStr = jsonMatch[0];
  }

  const extracted = JSON.parse(jsonStr);

  // Normalize phone number
  let phone = (extracted.phone || '').replace(/[^0-9+]/g, '');
  if (phone && !phone.startsWith('+')) {
    if (phone.length === 10) {
      phone = '+1' + phone;
    } else if (phone.length === 11 && phone.startsWith('1')) {
      phone = '+' + phone;
    }
  }

  return [{
    json: {
      first_name: extracted.first_name || '',
      last_name: extracted.last_name || '',
      email: extracted.email || '',
      phone: phone,
      address: extracted.address || '',
      city: extracted.city || '',
      state: extracted.state || '',
      intent: extracted.intent || 'unknown',
      source: $('Normalize Input').first().json.source || 'unknown',
      received_at: $('Normalize Input').first().json.received_at
    }
  }];
} catch (error) {
  return [{
    json: {
      error: 'Failed to parse AI response',
      raw_response: aiResponse,
      source: $('Normalize Input').first().json.source || 'unknown'
    }
  }];
}

// Calculate priority and merge data
const leadData = $('Parse AI Response').first().json;
const rentcastData = $input.first().json;

// Get home value (handle various response formats)
let homeValue = 0;
if (rentcastData.price) {
  homeValue = rentcastData.price;
} else if (rentcastData.priceRangeLow) {
  homeValue = rentcastData.priceRangeLow;
} else if (rentcastData.estimate) {
  homeValue = rentcastData.estimate;
}

// Calculate priority tier
let priority, tier, sequenceSpeed;
if (homeValue >= 500000) {
  priority = 'A';
  tier = 'High';
  sequenceSpeed = 'aggressive';
} else if (homeValue >= 300000) {
  priority = 'B';
  tier = 'Medium';
  sequenceSpeed = 'standard';
} else {
  priority = 'C';
  tier = 'Lower';
  sequenceSpeed = 'relaxed';
}

// Format home value for display
const homeValueFormatted = homeValue > 0 
  ? '$' + homeValue.toLocaleString('en-US')
  : 'Not available';

return [{
  json: {
    ...leadData,
    home_value: homeValue,
    home_value_formatted: homeValueFormatted,
    home_value_high: rentcastData.priceRangeHigh || null,
    confidence: rentcastData.confidence || 'unknown',
    priority: priority,
    tier: tier,
    sequence_speed: sequenceSpeed,
    rentcast_raw: rentcastData
  }
}];

// Merge HubSpot response with lead data
const leadData = $('Calculate Priority').first().json;
const hubspotContact = $input.first().json;

return [{
  json: {
    ...leadData,
    hubspot_contact_id: hubspotContact.id,
    hubspot_url: `https://app.hubspot.com/contacts/${hubspotContact.portalId || 'YOUR_PORTAL_ID'}/contact/${hubspotContact.id}`
  }
}];

